mapper.update:
UPDATE EMPLOYEES
   SET EMAIL = SUBSTR(#{email}, 1, INSTR(#{email}, '@emp.com')-1)
 WHERE EMPLOYEE_ID = #{empId}

UPDATE EMPLOYEES
   SET PHONE_NUMBER = #{phone}
   	 , SALARY = #{salary}
   	 , COMMISSION_PCT = #{commission}
   	 , HIRE_DATE = #{hireDate}
 WHERE EMPLOYEE_ID = #{empId}
